/* cambia l'icona dalla manina alla freccetta */
[class*="vote-"] :before {
    font-family: icons;
    speak: none;
    font-style: normal;
    font-weight: 400;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
}

a.vote-up span.control::before {
    content: "\f103"!important;
}

a.vote-down span.control::before {
    content: "\f102"!important;
}

.embed-refresh .post-votes .vote-up .control,
.embed-refresh .post-votes .vote-down .control {
    background-color: transparent !important;
    -webkit-mask-image: none !important;
}


/* risolve i problemi di impaginazione della lista di upvote */

.tooltip.voters a.username.username--refresh{
    font-size: 13px !important;
    padding-left: 35px !important;
    width: 150px !important;
} 

.tooltip.voters a.avatar.avatar--refresh{
    position:absolute
}

